import './DataTableDemo.css';
import axios from 'axios';

const DataTableCrudVaga = () => {

    let emptyVaga = {
        id: null,
        dtcad: 0,
        usercad: 0,
        ativo: 0,
        nome: '',
        descricao: null,
        salario: '',
        setor: null,
    };

    const [vagas, setVagas] = useState(null);
    const [vagaDialog, setVagaDialog] = useState(false);
    const [deleteVagaDialog, setDeleteVagaDialog] = useState(false);
    const [deleteVagasDialog, setDeleteVagasDialog] = useState(false);
    const [vaga, setVaga] = useState(emptyVaga);
    const [selectedVagas, setSelectedVagas] = useState(null);
    const [submitted, setSubmitted] = useState(false);
    const [globalFilter, setGlobalFilter] = useState(null);
    const toast = useRef(null);
    const dt = useRef(null);
    const load = useRef(null
    );
    const Api = axios.create({baseURL: 'http://localhost:8081/'});


    useEffect(() => {
        fetchAll();
    }, []);

    const fetchAll = () => {
        load.current = true;
        async function fetchAll() {
            const result = await Api.get("vagas")
            if (result.data) {
                setVagas(result.data)
            }
        }
        if (load.current) {
            fetchAll();
        }
        load.current = false;
    }

    const newVagas =()=>{
        load.current=true;
        async function newVaga(){
            const result = await Api.post("vagas",{usercad: "Usuário"})
            if(result.data){
                vaga.id = result.data.id
                setSubmitted(false);
                setVagaDialog(true);
            }
        }
        if(load.current){
            newVaga()
        }
        load.current=false
    }

    const saveDataVagas=(id)=>{
        load.current = true
        async function save(){
            const result = await Api.post(`vagas/${id}/atualizar`,
            {id: id,
                nome: vaga.nome,
                setor: vaga.setor,
                descricao: vaga.descricao,
                salario: vaga.salario,
                })
            if(result.data === true){
                setVaga(emptyVaga);
                setSubmitted(false);
                setVagaDialog(false);
                fetchAll();
                }
        }
        if(load.current){
            save()
        }
        load.current =false
    }

    const openNew = () => {
        newVagas()
    }

    const hideDialog = () => {
        setSubmitted(false);
        setVagaDialog(false);
    }

    const hideDeleteVagaDialog = () => {
        setDeleteVagaDialog(false);
    }

    const hideDeleteVagasDialog = () => {
        setDeleteVagasDialog(false);
    }

    const saveVaga = () => {
        setSubmitted(true);

        if (vaga.nome.trim()) {
            let _vagas = [...vagas];
         
            if (vaga.id) {
                const index = findIndexById(vaga.id);
                if(index > 0){

                    saveDataVagas(vaga.id)
                    toast.current.show({ severity: 'success', summary: 'Sucesso', detail: 'Vaga Atualizada', life: 3000 });
                }else{
                    saveDataVagas(vaga.id)
                    toast.current.show({ severity: 'success', summary: 'Sucesso', detail: 'Vaga Criada', life: 3000 });
            }
            setVaga(emptyVaga);
        }

            setVagas(_vagas);
            setVagaDialog(false);
        }
    }

   
    const editVaga = (vaga) => {
        setVaga({...vaga});
        setVagaDialog(true);
    }

    const confirmDeleteVaga = (vaga) => {
        setVaga(vaga);
        setDeleteVagaDialog(true);
    }

    const deleteVaga = () => {
        let _vagas = vagas.filter(val => val.id !== vaga.id);
        setVagas(_vagas);
        setDeleteVagaDialog(false);
        setVaga(emptyVaga);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Vaga Deletada', life: 3000 });
    }

    const findIndexById = (id) => {
        let index = -1;
        for (let i = 0; i < vagas.length; i++) {
            if (vagas[i].id === id) {
                index = i;
                break;
            }
        }

        return index;
    }

   
    const confirmDeleteSelected = () => {
        setDeleteVagasDialog(true);
    }

    const deleteSelectedVagas = () => {
        let _vagas = vagas.filter(val => !selectedVagas.includes(val));
        setVagas(_vagas);
        setDeleteVagasDialog(false);
        setSelectedVagas(null);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Vagas Deletada', life: 3000 });
    }

    const onInputChange = (e, nome) => {
        const val = (e.target && e.target.value) || '';
        let _vaga = {...vaga};
        _vaga[`${nome}`] = val;

        setVaga(_vaga);
    }


    const leftToolbarTemplate = () => {
        return (
            <React.Fragment>
                <Button label="Novo" icon="pi pi-plus" className="p-button-success mr-2" onClick={openNew} />
                <Button label="Deletar" icon="pi pi-trash" className="p-button-danger" onClick={confirmDeleteSelected} disabled={!selectedVagas || !selectedVagas.length} />
            </React.Fragment>
        )
    }

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" className="p-button-rounded p-button-success mr-2" onClick={() => editVaga(rowData)} />
                <Button icon="pi pi-trash" className="p-button-rounded p-button-warning" onClick={() => confirmDeleteVaga(rowData)} />
            </React.Fragment>
        );
    }

    const header = (
        <div className="table-header">
            <h5 className="mx-0 my-1">Gerenciamento de Vagas</h5>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );
    const vagaDialogFooter = (
        <React.Fragment>
            <Button label="Cancel" icon="pi pi-times" className="p-button-text" onClick={hideDialog} />
            <Button label="Save" icon="pi pi-check" className="p-button-text" onClick={saveVaga} />
        </React.Fragment>
    );
    const deleteVagaDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteVagaDialog} />
            <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={deleteVaga} />
        </React.Fragment>
    );
    const deleteVagasDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteVagasDialog} />
            <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={deleteSelectedVagas} />
        </React.Fragment>
    );

    const formatCurrency = (value) => {
        return value.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' });
    }

    const salarioBodyTemplate = (rowData) => {
        return formatCurrency(rowData.salario);
    }

    return (
        <div className="datatable-crud-demo">
            <Toast ref={toast} />

            <div className="card">
                <Toolbar className="mb-4" left={leftToolbarTemplate}></Toolbar>

                <DataTable ref={dt} value={vagas} selection={selectedVagas} onSelectionChange={(e) => setSelectedVagas(e.value)}
                    dataKey="id" paginator rows={10} rowsPerPageOptions={[5, 10, 25]}
                    paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                    currentPageReportTemplate="Showing {first} to {last} of {totalRecords} vagas"
                    globalFilter={globalFilter} header={header} responsiveLayout="scroll">
                    <Column selectionMode="multiple" headerStyle={{ width: '3rem' }} exportable={false}></Column>
                    <Column field="id" header="Codigo" sortable></Column>
                    <Column field="nome" header="Nome" sortable></Column>
                    <Column field="descricao" header="Descrição"></Column>
                    <Column field="setor" header="Setor" sortable></Column>
                    <Column field="salario" body={salarioBodyTemplate} header="Salário" sortable ></Column>
                    <Column body={actionBodyTemplate} exportable={false} style={{ minWidth: '8rem' }}></Column>
                </DataTable>
            </div>

            <Dialog visible={vagaDialog} style={{ width: '450px' }} header="Destalhes Vaga" modal className="p-fluid" footer={vagaDialogFooter} onHide={hideDialog}>
                <div className="field">
                    <label htmlFor="nome">Nome</label>
                    <InputText id="nome" value={vaga.nome} onChange={(e) => onInputChange(e, 'nome')} required autoFocus className={classNames({ 'p-invalid': submitted && !vaga.nome })} />
                    {submitted && !vaga.nome && <small className="p-error">Nome é obrigatório.</small>}
                </div>
                <div className="field">
                    <label htmlFor="descricao">Descrição</label>
                    <InputTextarea id="descricao" value={vaga.descricao} onChange={(e) => onInputChange(e, 'descricao')} required autoFocus className={classNames({ 'p-invalid': submitted && !vaga.descricao })} />
                    {submitted && !vaga.descricao && <small className="p-error">Descrição é obrigatório.</small>}
                </div>
                <div className="field">
                    <label htmlFor="setor">Setor</label>
                    <InputText id="setor" value={vaga.setor} onChange={(e) => onInputChange(e, 'setor')} required autoFocus className={classNames({ 'p-invalid': submitted && !vaga.setor })} />
                    {submitted && !vaga.setor && <small className="p-error">Setor é obrigatório.</small>}
                </div>
                <div className="field">
                    <label htmlFor="salario">Salário</label>
                    <InputNumber id="price" value={product.price} onValueChange={(e) => onInputNumberChange(e, 'price')} mode="currency" currency="USD" locale="en-US" />
                    <InputText id="salario" value={vaga.salario} onChange={(e) => onInputChange(e, 'salario')} required autoFocus className={classNames({ 'p-invalid': submitted && !vaga.salario })} />
                    {submitted && !vaga.salario && <small className="p-error">Salário é obrigatório.</small>}
                </div>
            </Dialog>

            <Dialog visible={deleteVagaDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteVagaDialogFooter} onHide={hideDeleteVagaDialog}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem'}} />
                    {vaga && <span>Are you sure you want to delete <b>{vaga.nome}</b>?</span>}
                </div>
            </Dialog>

            <Dialog visible={deleteVagasDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteVagasDialogFooter} onHide={hideDeleteVagasDialog}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem'}} />
                    {vaga && <span>Are you sure you want to delete the selected vagas?</span>}
                </div>
            </Dialog>
        </div>
    );
}
                 
export default function Vagas() {
    return <div className="card">
        <div className="card text-center"><h3>Cadastro de Vagas</h3></div>
        < DataTableCrudVaga />
    </div>
}
