import React, { useState, useEffect, useRef } from 'react';
import { classNames } from 'primereact/utils';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Toast } from 'primereact/toast';
import { Button } from 'primereact/button';
import { FileUpload } from 'primereact/fileupload';
import { Rating } from 'primereact/rating';
import { Toolbar } from 'primereact/toolbar';
import { InputTextarea } from 'primereact/inputtextarea';
import { RadioButton } from 'primereact/radiobutton';
import { InputNumber } from 'primereact/inputnumber';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import './DataTableDemo.css';

const DataTableCrudDemo = () => {

    let emptyCandidatura = {
        id: "",
        usercad: '',
        dtcad: '',
        ativo: '',
        idvaga: '',
        idcandidato: '',
        aprovado: '',
        observacoes: '',
        status: '',
    };

    const [candidaturas, setCandidaturas] = useState(null);
    const [candidaturaDialog, setCandidaturaDialog] = useState(false);
    const [deleteCandidaturaDialog, setDeleteCandidaturaDialog] = useState(false);
    const [deleteCandidaturasDialog, setDeleteCandidaturasDialog] = useState(false);
    const [candidatura, setCandidatura] = useState(emptyCandidatura);
    const [selectedCandidaturas, setSelectedCandidaturas] = useState(null);
    const [submitted, setSubmitted] = useState(false);
    const [globalFilter, setGlobalFilter] = useState(null);
    const toast = useRef(null);
    const dt = useRef(null);

    useEffect(() => {
    }, []);

    const formatCurrency = (value) => {
        return value.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
    }

    const openNew = () => {
        setCandidatura(emptyCandidatura);
        setSubmitted(false);
        setCandidaturaDialog(true);
    }

    const hideDialog = () => {
        setSubmitted(false);
        setCandidaturaDialog(false);
    }

    const hideDeleteCandidaturaDialog = () => {
        setDeleteCandidaturaDialog(false);
    }

    const hideDeleteCandidaturasDialog = () => {
        setDeleteCandidaturasDialog(false);
    }

    const saveCandidatura = () => {
        setSubmitted(true);

        if (candidatura.name.trim()) {
            let _candidaturas = [...candidaturas];
            let _candidatura = {...candidatura};
            if (candidatura.id) {
                const index = findIndexById(candidatura.id);

                _candidaturas[index] = _candidatura;
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Candidatura Updated', life: 3000 });
            }
            else {
                _candidatura.image = 'candidatura-placeholder.svg';
                _candidaturas.push(_candidatura);
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Candidatura Created', life: 3000 });
            }

            setCandidaturas(_candidaturas);
            setCandidaturaDialog(false);
            setCandidatura(emptyCandidatura);
        }
    }

    const editCandidatura = (candidatura) => {
        setCandidatura({...candidatura});
        setCandidaturaDialog(true);
    }

    const confirmDeleteCandidatura = (candidatura) => {
        setCandidatura(candidatura);
        setDeleteCandidaturaDialog(true);
    }

    const deleteCandidatura = () => {
        let _candidaturas = candidaturas.filter(val => val.id !== candidatura.id);
        setCandidaturas(_candidaturas);
        setDeleteCandidaturaDialog(false);
        setCandidatura(emptyCandidatura);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Candidatura Deleted', life: 3000 });
    }

    const findIndexById = (id) => {
        let index = -1;
        for (let i = 0; i < candidaturas.length; i++) {
            if (candidaturas[i].id === id) {
                index = i;
                break;
            }
        }

        return index;
    }


    const confirmDeleteSelected = () => {
        setDeleteCandidaturasDialog(true);
    }

    const deleteSelectedCandidaturas = () => {
        let _candidaturas = candidaturas.filter(val => !selectedCandidaturas.includes(val));
        setCandidaturas(_candidaturas);
        setDeleteCandidaturasDialog(false);
        setSelectedCandidaturas(null);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Candidaturas Deleted', life: 3000 });
    }

    const onInputChange = (e, name) => {
        const val = (e.target && e.target.value) || '';
        let _candidatura = {...candidatura};
        _candidatura[`${name}`] = val;

        setCandidatura(_candidatura);
    }

    const onInputNumberChange = (e, name) => {
        const val = e.value || 0;
        let _candidatura = {...candidatura};
        _candidatura[`${name}`] = val;

        setCandidatura(_candidatura);
    }

    const leftToolbarTemplate = () => {
        return (
            <React.Fragment>
                <Button label="New" icon="pi pi-plus" className="p-button-success mr-2" onClick={openNew} />
                <Button label="Delete" icon="pi pi-trash" className="p-button-danger" onClick={confirmDeleteSelected} disabled={!selectedCandidaturas || !selectedCandidaturas.length} />
            </React.Fragment>
        )
    }

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" className="p-button-rounded p-button-success mr-2" onClick={() => editCandidatura(rowData)} />
                <Button icon="pi pi-trash" className="p-button-rounded p-button-warning" onClick={() => confirmDeleteCandidatura(rowData)} />
            </React.Fragment>
        );
    }

    const header = (
        <div className="table-header">
            <h5 className="mx-0 my-1">Manage Candidaturas</h5>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );
    const candidaturaDialogFooter = (
        <React.Fragment>
            <Button label="Cancel" icon="pi pi-times" className="p-button-text" onClick={hideDialog} />
            <Button label="Save" icon="pi pi-check" className="p-button-text" onClick={saveCandidatura} />
        </React.Fragment>
    );
    const deleteCandidaturaDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteCandidaturaDialog} />
            <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={deleteCandidatura} />
        </React.Fragment>
    );
    const deleteCandidaturasDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteCandidaturasDialog} />
            <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={deleteSelectedCandidaturas} />
        </React.Fragment>
    );

    return (
        <div className="datatable-crud-demo">
            <Toast ref={toast} />

            <div className="card">
                <Toolbar className="mb-4" left={leftToolbarTemplate}></Toolbar>

                <DataTable ref={dt} value={candidaturas} selection={selectedCandidaturas} onSelectionChange={(e) => setSelectedCandidaturas(e.value)}
                    dataKey="id" paginator rows={10} rowsPerPageOptions={[5, 10, 25]}
                    paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                    currentPageReportTemplate="Showing {first} to {last} of {totalRecords} candidaturas"
                    globalFilter={globalFilter} header={header} responsiveLayout="scroll">
                    <Column selectionMode="multiple" headerStyle={{ width: '3rem' }} exportable={false}></Column>
                    <Column field="code" header="Code" sortable style={{ minWidth: '12rem' }}></Column>
                    <Column field="name" header="Name" sortable style={{ minWidth: '16rem' }}></Column>
                    
                    <Column field="category" header="Category" sortable style={{ minWidth: '10rem' }}></Column>
                    <Column body={actionBodyTemplate} exportable={false} style={{ minWidth: '8rem' }}></Column>
                </DataTable>
            </div>

            <Dialog visible={candidaturaDialog} style={{ width: '450px' }} header="Candidatura Details" modal className="p-fluid" footer={candidaturaDialogFooter} onHide={hideDialog}>
            <div className="col-12 md:col-6 lg:col-2">
                    <label htmlFor="sexo">Sexo</label>
                    <Dropdown value={selectedSexo} options={sexoOpcoes} onChange={(e)=>setSelectedSexo(e.value)} optionLabel="name" className={classNames({ 'p-invalid': submitted && !selectedSexo })}/>
                    {submitted && !selectedSexo && <small className="p-error">IDADE is required.</small>}
                </div>
                <div className="field">
                    <label htmlFor="name">Name</label>
                    <InputText id="name" value={candidatura.name} onChange={(e) => onInputChange(e, 'name')} required autoFocus className={classNames({ 'p-invalid': submitted && !candidatura.name })} />
                    {submitted && !candidatura.name && <small className="p-error">Name is required.</small>}
                </div>
                <div className="field">
                    <label htmlFor="description">Description</label>
                    <InputTextarea id="description" value={candidatura.description} onChange={(e) => onInputChange(e, 'description')} required rows={3} cols={20} />
                </div>
            </Dialog>

            <Dialog visible={deleteCandidaturaDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteCandidaturaDialogFooter} onHide={hideDeleteCandidaturaDialog}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem'}} />
                    {candidatura && <span>Are you sure you want to delete <b>{candidatura.name}</b>?</span>}
                </div>
            </Dialog>

            <Dialog visible={deleteCandidaturasDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteCandidaturasDialogFooter} onHide={hideDeleteCandidaturasDialog}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem'}} />
                    {candidatura && <span>Are you sure you want to delete the selected candidaturas?</span>}
                </div>
            </Dialog>
        </div>
    );
}
 
                
export default function Candidaturas() {
    return <div className="card">
        <div className="card text-center"><h3>Candidaturas</h3></div>
        <DataTableCrudDemo/>
    </div>
}
