import React, { useState, useEffect, useRef } from 'react';
import { classNames } from 'primereact/utils';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Toast } from 'primereact/toast';
import { Button } from 'primereact/button';
import { FileUpload } from 'primereact/fileupload';
import { Toolbar } from 'primereact/toolbar';
import { InputTextarea } from 'primereact/inputtextarea';
import { RadioButton } from 'primereact/radiobutton';
import { InputNumber } from 'primereact/inputnumber';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import './DataTableDemo.css';
import axios from 'axios';

const DataTableCrudVaga = () => {

    let emptyVaga = {
        id: null,
        price: 0,
        quantity: 0,
        rating: 0,
        nome: '',
        descricao: null,
        salario: '',
        setor: null,
        inventoryStatus: 'INSTOCK'
    };

    const [vagas, setVagas] = useState(null);
    const [vagaDialog, setVagaDialog] = useState(false);
    const [deleteVagaDialog, setDeleteVagaDialog] = useState(false);
    const [deleteVagasDialog, setDeleteVagasDialog] = useState(false);
    const [vaga, setVaga] = useState(emptyVaga);
    const [selectedVagas, setSelectedVagas] = useState(null);
    const [submitted, setSubmitted] = useState(false);
    const [globalFilter, setGlobalFilter] = useState(null);
    const toast = useRef(null);
    const dt = useRef(null);
    const load = useRef(null
    );
    const Api = axios.create({baseURL: 'http://localhost:8081/'});


    useEffect(() => {
    }, []);

    const openNew = () => {
        setVaga(emptyVaga);
        setSubmitted(false);
        setVagaDialog(true);
    }

    const hideDialog = () => {
        setSubmitted(false);
        setVagaDialog(false);
    }

    const hideDeleteVagaDialog = () => {
        setDeleteVagaDialog(false);
    }

    const hideDeleteVagasDialog = () => {
        setDeleteVagasDialog(false);
    }

    const saveVaga = () => {
        setSubmitted(true);

        if (vaga.nome.trim()) {
            let _vagas = [...vagas];
            let _vaga = {...vaga};
            if (vaga.id) {
                const index = findIndexById(vaga.id);

                _vagas[index] = _vaga;
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Vaga Atualizada', life: 3000 });
            }
            else {
                _vaga.id = createId();
                _vaga.descricao = 'vaga-placeholder.svg';
                _vagas.push(_vaga);
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Vaga Criada', life: 3000 });
            }

            setVagas(_vagas);
            setVagaDialog(false);
            setVaga(emptyVaga);
        }
    }

    const editVaga = (vaga) => {
        setVaga({...vaga});
        setVagaDialog(true);
    }

    const confirmDeleteVaga = (vaga) => {
        setVaga(vaga);
        setDeleteVagaDialog(true);
    }

    const deleteVaga = () => {
        let _vagas = vagas.filter(val => val.id !== vaga.id);
        setVagas(_vagas);
        setDeleteVagaDialog(false);
        setVaga(emptyVaga);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Vaga Deletada', life: 3000 });
    }

    const findIndexById = (id) => {
        let index = -1;
        for (let i = 0; i < vagas.length; i++) {
            if (vagas[i].id === id) {
                index = i;
                break;
            }
        }

        return index;
    }

    const createId = () => {
        let id = '';
        let chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        for (let i = 0; i < 5; i++) {
            id += chars.charAt(Math.floor(Math.random() * chars.length));
        }
        return id;
    }

    const importCSV = (e) => {
        const file = e.files[0];
        const reader = new FileReader();
        reader.onload = (e) => {
            const csv = e.target.result;
            const data = csv.split('\n');

            // Prepare DataTable
            const cols = data[0].replace(/['"]+/g, '').split(',');
            data.shift();

            const importedData = data.map(d => {
                d = d.split(',');
                const processedData = cols.reduce((obj, c, i) => {
                    c = c === 'Status' ? 'inventoryStatus' : (c === 'Reviews' ? 'rating' : c.toLowerCase());
                    obj[c] = d[i].replace(/['"]+/g, '');
                    (c === 'price' || c === 'rating') && (obj[c] = parseFloat(obj[c]));
                    return obj;
                }, {});

                processedData['id'] = createId();
                return processedData;
            });

            const _vagas = [...vagas, ...importedData];

            setVagas(_vagas);
        };

        reader.readAsText(file, 'UTF-8');
    }

    const exportCSV = () => {
        dt.current.exportCSV();
    }

    const confirmDeleteSelected = () => {
        setDeleteVagasDialog(true);
    }

    const deleteSelectedVagas = () => {
        let _vagas = vagas.filter(val => !selectedVagas.includes(val));
        setVagas(_vagas);
        setDeleteVagasDialog(false);
        setSelectedVagas(null);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Vagas Deletada', life: 3000 });
    }

    const onCategoryChange = (e) => {
        let _vaga = {...vaga};
        _vaga['setor'] = e.value;
        setVaga(_vaga);
    }

    const onInputChange = (e, nome) => {
        const val = (e.target && e.target.value) || '';
        let _vaga = {...vaga};
        _vaga[`${nome}`] = val;

        setVaga(_vaga);
    }

    const onInputNumberChange = (e, nome) => {
        const val = e.value || 0;
        let _vaga = {...vaga};
        _vaga[`${nome}`] = val;

        setVaga(_vaga);
    }

    const leftToolbarTemplate = () => {
        return (
            <React.Fragment>
                <Button label="New" icon="pi pi-plus" className="p-button-success mr-2" onClick={openNew} />
                <Button label="Delete" icon="pi pi-trash" className="p-button-danger" onClick={confirmDeleteSelected} disabled={!selectedVagas || !selectedVagas.length} />
            </React.Fragment>
        )
    }

    const rightToolbarTemplate = () => {
        return (
            <React.Fragment>
                <FileUpload mode="basic" nome="demo[]" auto url="https://primefaces.org/primereact/showcase/upload.php" accept=".csv" chooseLabel="Import" className="mr-2 inline-block" onUpload={importCSV} />
                <Button label="Export" icon="pi pi-upload" className="p-button-help" onClick={exportCSV} />
            </React.Fragment>
        )
    }

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" className="p-button-rounded p-button-success mr-2" onClick={() => editVaga(rowData)} />
                <Button icon="pi pi-trash" className="p-button-rounded p-button-warning" onClick={() => confirmDeleteVaga(rowData)} />
            </React.Fragment>
        );
    }

    const header = (
        <div className="table-header">
            <h5 className="mx-0 my-1">Manage Vagas</h5>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );
    const vagaDialogFooter = (
        <React.Fragment>
            <Button label="Cancel" icon="pi pi-times" className="p-button-text" onClick={hideDialog} />
            <Button label="Save" icon="pi pi-check" className="p-button-text" onClick={saveVaga} />
        </React.Fragment>
    );
    const deleteVagaDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteVagaDialog} />
            <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={deleteVaga} />
        </React.Fragment>
    );
    const deleteVagasDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteVagasDialog} />
            <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={deleteSelectedVagas} />
        </React.Fragment>
    );

    return (
        <div className="datatable-crud-demo">
            <Toast ref={toast} />

            <div className="card">
                <Toolbar className="mb-4" left={leftToolbarTemplate} right={rightToolbarTemplate}></Toolbar>

                <DataTable ref={dt} value={vagas} selection={selectedVagas} onSelectionChange={(e) => setSelectedVagas(e.value)}
                    dataKey="id" paginator rows={10} rowsPerPageOptions={[5, 10, 25]}
                    paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                    currentPageReportTemplate="Showing {first} to {last} of {totalRecords} vagas"
                    globalFilter={globalFilter} header={header} responsiveLayout="scroll">
                    <Column selectionMode="multiple" headerStyle={{ width: '3rem' }} exportable={false}></Column>
                    <Column field="code" header="Code" sortable></Column>
                    <Column field="nome" header="Name" sortable></Column>
                    <Column field="setor" header="Category" sortable></Column>
                    <Column field="rating" header="Reviews" sortable ></Column>
                    <Column body={actionBodyTemplate} exportable={false} style={{ minWidth: '8rem' }}></Column>
                </DataTable>
            </div>

            <Dialog visible={vagaDialog} style={{ width: '450px' }} header="Vaga Details" modal className="p-fluid" footer={vagaDialogFooter} onHide={hideDialog}>
                {vaga.descricao && <img src={`descricaos/vaga/${vaga.descricao}`} onError={(e) => e.target.src='https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={vaga.descricao} className="vaga-descricao block m-auto pb-3" />}
                <div className="field">
                    <label htmlFor="nome">Name</label>
                    <InputText id="nome" value={vaga.nome} onChange={(e) => onInputChange(e, 'nome')} required autoFocus className={classNames({ 'p-invalid': submitted && !vaga.nome })} />
                    {submitted && !vaga.nome && <small className="p-error">Name is required.</small>}
                </div>
                <div className="field">
                    <label htmlFor="salario">Description</label>
                    <InputTextarea id="salario" value={vaga.salario} onChange={(e) => onInputChange(e, 'salario')} required rows={3} cols={20} />
                </div>

                <div className="field">
                    <label className="mb-3">Category</label>
                    <div className="formgrid grid">
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="setor1" nome="setor" value="Accessories" onChange={onCategoryChange} checked={vaga.setor === 'Accessories'} />
                            <label htmlFor="setor1">Accessories</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="setor2" nome="setor" value="Clothing" onChange={onCategoryChange} checked={vaga.setor === 'Clothing'} />
                            <label htmlFor="setor2">Clothing</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="setor3" nome="setor" value="Electronics" onChange={onCategoryChange} checked={vaga.setor === 'Electronics'} />
                            <label htmlFor="setor3">Electronics</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="setor4" nome="setor" value="Fitness" onChange={onCategoryChange} checked={vaga.setor === 'Fitness'} />
                            <label htmlFor="setor4">Fitness</label>
                        </div>
                    </div>
                </div>

                <div className="formgrid grid">
                    <div className="field col">
                        <label htmlFor="price">Price</label>
                        <InputNumber id="price" value={vaga.price} onValueChange={(e) => onInputNumberChange(e, 'price')} mode="currency" currency="USD" locale="en-US" />
                    </div>
                    <div className="field col">
                        <label htmlFor="quantity">Quantity</label>
                        <InputNumber id="quantity" value={vaga.quantity} onValueChange={(e) => onInputNumberChange(e, 'quantity')} integeronly />
                    </div>
                </div>
            </Dialog>

            <Dialog visible={deleteVagaDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteVagaDialogFooter} onHide={hideDeleteVagaDialog}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem'}} />
                    {vaga && <span>Are you sure you want to delete <b>{vaga.nome}</b>?</span>}
                </div>
            </Dialog>

            <Dialog visible={deleteVagasDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteVagasDialogFooter} onHide={hideDeleteVagasDialog}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem'}} />
                    {vaga && <span>Are you sure you want to delete the selected vagas?</span>}
                </div>
            </Dialog>
        </div>
    );
}
                 
export default function Vagas() {
    return <div className="card">
        <div className="card text-center"><h3>Cadastro de Vagas</h3></div>
        < DataTableCrudVaga />
    </div>
}
