import React, { useState , useRef} from 'react';
import { Chart } from 'primereact/chart';
import axios from 'axios';
import { useEffect } from 'react';

const LineChartDemo = () => {
    const Api = axios.create({baseURL: 'http://localhost:8081/'});
    const load = useRef(null)

    useEffect(() => {
        fetchAll()
    })

    let dados = []
    let vagasnome = []
    const fetchAll = () => {
        load.current = true;
        async function fetchAll() {
            const result = await Api.get("candidaturas")
            if (result.data) {
                dados = result.data
                
                for (var x in y) {

                    vagasnome[y[x]] = vagasnome[y[x]]!=undefined ? vagasnome[y[x]]+1 : 1;
                    }

                Array.from(dados).forEach(val => {
                    if (!vagasnome.contains(val.nomevaga)) {
                        vagasnome.push(val.nomevaga);    
                    }
                })
            }
        }
        if (load.current) {
            fetchAll();
        }
        load.current = false;
    }

    const [lineStylesData] = useState({
        labels: vagasnome,
        datasets: [
            {
                label: 'Vagas Cadastradas',
                data: [65, 59, 80, 81, 56, 55, 40],
                fill: false,
                tension: .4,
                borderColor: '#42A5F5'
            },
            {
                label: 'Candidatos',
                data: [28, 48, 40, 19, 86, 27, 90],
                fill: false,
                borderDash: [5, 5],
                tension: .4,
                borderColor: '#66BB6A'
            },
            {
                label: 'Candidatos a por vagas',
                data: [12, 51, 62, 33, 21, 62, 45],
                fill: true,
                borderColor: '#FFA726',
                tension: .4,
                backgroundColor: 'rgba(255,167,38,0.2)'
            }
        ]
    });

    const getLightTheme = () => {
        let basicOptions = {
            maintainAspectRatio: false,
            aspectRatio: .6,
            plugins: {
                legend: {
                    labels: {
                        color: '#495057'
                    }
                }
            },
            scales: {
                x: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                },
                y: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                }
            }
        };

        let multiAxisOptions = {
            stacked: false,
            maintainAspectRatio: false,
            aspectRatio: .6,
            plugins: {
                legend: {
                    labels: {
                        color: '#495057'
                    }
                }
            },
            scales: {
                x: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                },
                y: {
                    type: 'linear',
                    display: true,
                    position: 'left',
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                },
                y1: {
                    type: 'linear',
                    display: true,
                    position: 'right',
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        drawOnChartArea: false,
                        color: '#ebedef'
                    }
                }
            }
        };

        return {
            basicOptions,
            multiAxisOptions
        }
    }

    const { basicOptions} = getLightTheme();

    return (
        <div>
            <div className="card">
            <div className="grid">
            <div className="col-1"></div>
            <div className="col-10 md:col-10 lg:col-10">
                <h5>Paínel</h5>
                <Chart type="line" data={lineStylesData} options={basicOptions} />
            </div>
            </div>
            </div>
        </div>
    )
}
                 
                 
export default function Home() {
    return <div className="card">
        < LineChartDemo />
       </div>
}
