import React, { useState, useEffect, useRef } from 'react';
import { classNames } from 'primereact/utils';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Toast } from 'primereact/toast';
import { Button } from 'primereact/button';
import { FileUpload } from 'primereact/fileupload';
import { Rating } from 'primereact/rating';
import { Toolbar } from 'primereact/toolbar';
import { InputTextarea } from 'primereact/inputtextarea';
import { RadioButton } from 'primereact/radiobutton';
import { InputNumber } from 'primereact/inputnumber';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import './DataTableDemo.css';

import Loading from '../Layout/Loading'

const DataTableCrudDemo = () => {

    let emptyCandidato = {
        id: null,
        name: '',
        image: null,
        description: '',
        category: null,
        price: 0,
        quantity: 0,
        rating: 0,
        inventoryStatus: 'INSTOCK'
    };

    const [candidatos, setCandidatos] = useState(null);
    const [candidatoDialog, setCandidatoDialog] = useState(false);
    const [deleteCandidatoDialog, setDeleteCandidatoDialog] = useState(false);
    const [deleteCandidatosDialog, setDeleteCandidatosDialog] = useState(false);
    const [candidato, setCandidato] = useState(emptyCandidato);
    const [selectedCandidatos, setSelectedCandidatos] = useState(null);
    const [submitted, setSubmitted] = useState(false);
    const [globalFilter, setGlobalFilter] = useState(null);
    const [removeLoading, setRemoveLoading] = useState(false)
    const toast = useRef(null);
    const dt = useRef(null);

    const fetchCandidatos=()=>{

    }

    useEffect(() => {
    }, []); 

    const openNew = () => {
        setCandidato(emptyCandidato);
        setSubmitted(false);
        setCandidatoDialog(true);
    }

    const hideDialog = () => {
        setSubmitted(false);
        setCandidatoDialog(false);
    }

    const hideDeleteCandidatoDialog = () => {
        setDeleteCandidatoDialog(false);
    }

    const hideDeleteCandidatosDialog = () => {
        setDeleteCandidatosDialog(false);
    }

    const saveCandidato = () => {
        setSubmitted(true);

        if (candidato.name.trim()) {
            let _candidatos = [...candidatos];
            let _candidato = {...candidato};
            if (candidato.id) {
                const index = findIndexById(candidato.id);

                _candidatos[index] = _candidato;
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Candidato Updated', life: 3000 });
            }
            else {
                _candidato.id = createId();
                _candidato.image = 'candidato-placeholder.svg';
                _candidatos.push(_candidato);
                toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Candidato Created', life: 3000 });
            }

            setCandidatos(_candidatos);
            setCandidatoDialog(false);
            setCandidato(emptyCandidato);
        }
    }

    const editCandidato = (candidato) => {
        setCandidato({...candidato});
        setCandidatoDialog(true);
    }

    const confirmDeleteCandidato = (candidato) => {
        setCandidato(candidato);
        setDeleteCandidatoDialog(true);
    }

    const deleteCandidato = () => {
        let _candidatos = candidatos.filter(val => val.id !== candidato.id);
        setCandidatos(_candidatos);
        setDeleteCandidatoDialog(false);
        setCandidato(emptyCandidato);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Candidato Deleted', life: 3000 });
    }

    const findIndexById = (id) => {
        let index = -1;
        for (let i = 0; i < candidatos.length; i++) {
            if (candidatos[i].id === id) {
                index = i;
                break;
            }
        }

        return index;
    }

    const createId = () => {
        let id = '';
        let chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        for (let i = 0; i < 5; i++) {
            id += chars.charAt(Math.floor(Math.random() * chars.length));
        }
        return id;
    }

    const importCSV = (e) => {
        const file = e.files[0];
        const reader = new FileReader();
        reader.onload = (e) => {
            const csv = e.target.result;
            const data = csv.split('\n');

            // Prepare DataTable
            const cols = data[0].replace(/['"]+/g, '').split(',');
            data.shift();

            const importedData = data.map(d => {
                d = d.split(',');
                const processedData = cols.reduce((obj, c, i) => {
                    c = c === 'Status' ? 'inventoryStatus' : (c === 'Reviews' ? 'rating' : c.toLowerCase());
                    obj[c] = d[i].replace(/['"]+/g, '');
                    (c === 'price' || c === 'rating') && (obj[c] = parseFloat(obj[c]));
                    return obj;
                }, {});

                processedData['id'] = createId();
                return processedData;
            });

            const _candidatos = [...candidatos, ...importedData];

            setCandidatos(_candidatos);
        };

        reader.readAsText(file, 'UTF-8');
    }

    const exportCSV = () => {
        dt.current.exportCSV();
    }

    const confirmDeleteSelected = () => {
        setDeleteCandidatosDialog(true);
    }

    const deleteSelectedCandidatos = () => {
        let _candidatos = candidatos.filter(val => !selectedCandidatos.includes(val));
        setCandidatos(_candidatos);
        setDeleteCandidatosDialog(false);
        setSelectedCandidatos(null);
        toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Candidatos Deleted', life: 3000 });
    }

    const onCategoryChange = (e) => {
        let _candidato = {...candidato};
        _candidato['category'] = e.value;
        setCandidato(_candidato);
    }

    const onInputChange = (e, name) => {
        const val = (e.target && e.target.value) || '';
        let _candidato = {...candidato};
        _candidato[`${name}`] = val;

        setCandidato(_candidato);
    }

    const onInputNumberChange = (e, name) => {
        const val = e.value || 0;
        let _candidato = {...candidato};
        _candidato[`${name}`] = val;

        setCandidato(_candidato);
    }

    const leftToolbarTemplate = () => {
        return (
            <React.Fragment>
                <Button label="New" icon="pi pi-plus" className="p-button-success mr-2" onClick={openNew} />
                <Button label="Delete" icon="pi pi-trash" className="p-button-danger" onClick={confirmDeleteSelected} disabled={!selectedCandidatos || !selectedCandidatos.length} />
            </React.Fragment>
        )
    }

    const rightToolbarTemplate = () => {
        return (
            <React.Fragment>
                <FileUpload mode="basic" name="demo[]" auto url="https://primefaces.org/primereact/showcase/upload.php" accept=".csv" chooseLabel="Import" className="mr-2 inline-block" onUpload={importCSV} />
                <Button label="Export" icon="pi pi-upload" className="p-button-help" onClick={exportCSV} />
            </React.Fragment>
        )
    }

    const imageBodyTemplate = (rowData) => {
        return <img src={`images/candidato/${rowData.image}`} onError={(e) => e.target.src='https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={rowData.image} className="candidato-image" />
    }

    const ratingBodyTemplate = (rowData) => {
        return <Rating value={rowData.rating} readOnly cancel={false} />;
    }

    const statusBodyTemplate = (rowData) => {
        return <span className={`candidato-badge status-${rowData.inventoryStatus.toLowerCase()}`}>{rowData.inventoryStatus}</span>;
    }

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" className="p-button-rounded p-button-success mr-2" onClick={() => editCandidato(rowData)} />
                <Button icon="pi pi-trash" className="p-button-rounded p-button-warning" onClick={() => confirmDeleteCandidato(rowData)} />
            </React.Fragment>
        );
    }

    const header = (
        <div className="table-header">
            <h5 className="mx-0 my-1">Manage Candidatos</h5>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );
    const candidatoDialogFooter = (
        <React.Fragment>
            <Button label="Cancel" icon="pi pi-times" className="p-button-text" onClick={hideDialog} />
            <Button label="Save" icon="pi pi-check" className="p-button-text" onClick={saveCandidato} />
        </React.Fragment>
    );
    const deleteCandidatoDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteCandidatoDialog} />
            <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={deleteCandidato} />
        </React.Fragment>
    );
    const deleteCandidatosDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" className="p-button-text" onClick={hideDeleteCandidatosDialog} />
            <Button label="Yes" icon="pi pi-check" className="p-button-text" onClick={deleteSelectedCandidatos} />
        </React.Fragment>
    );

    return (
        <div className="datatable-crud-demo">
            <Toast ref={toast} />

            {!removeLoading && <Loading/>}
{removeLoading && candidatos.length === 0 && (
    <p>Sem candidatos!</p>
)}

            <div className="card">
                <Toolbar className="mb-4" left={leftToolbarTemplate} right={rightToolbarTemplate}></Toolbar>

                <DataTable ref={dt} value={candidatos} selection={selectedCandidatos} onSelectionChange={(e) => setSelectedCandidatos(e.value)}
                    dataKey="id" paginator rows={10} rowsPerPageOptions={[5, 10, 25]}
                    paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                    currentPageReportTemplate="Showing {first} to {last} of {totalRecords} candidatos"
                    globalFilter={globalFilter} header={header} responsiveLayout="scroll">
                    <Column selectionMode="multiple" headerStyle={{ width: '3rem' }} exportable={false}></Column>
                    <Column field="code" header="Code" sortable style={{ minWidth: '12rem' }}></Column>
                    <Column field="name" header="Name" sortable style={{ minWidth: '16rem' }}></Column>
                    <Column field="image" header="Image" body={imageBodyTemplate}></Column>
                    <Column field="category" header="Category" sortable style={{ minWidth: '10rem' }}></Column>
                    <Column field="rating" header="Reviews" body={ratingBodyTemplate} sortable style={{ minWidth: '12rem' }}></Column>
                    <Column field="inventoryStatus" header="Status" body={statusBodyTemplate} sortable style={{ minWidth: '12rem' }}></Column>
                    <Column body={actionBodyTemplate} exportable={false} style={{ minWidth: '8rem' }}></Column>
                </DataTable>
            </div>

            <Dialog visible={candidatoDialog} style={{ width: '450px' }} header="Candidato Details" modal className="p-fluid" footer={candidatoDialogFooter} onHide={hideDialog}>
                {candidato.image && <img src={`images/candidato/${candidato.image}`} onError={(e) => e.target.src='https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={candidato.image} className="candidato-image block m-auto pb-3" />}
                <div className="field">
                    <label htmlFor="name">Name</label>
                    <InputText id="name" value={candidato.name} onChange={(e) => onInputChange(e, 'name')} required autoFocus className={classNames({ 'p-invalid': submitted && !candidato.name })} />
                    {submitted && !candidato.name && <small className="p-error">Name is required.</small>}
                </div>
                <div className="field">
                    <label htmlFor="description">Description</label>
                    <InputTextarea id="description" value={candidato.description} onChange={(e) => onInputChange(e, 'description')} required rows={3} cols={20} />
                </div>

                <div className="field">
                    <label className="mb-3">Category</label>
                    <div className="formgrid grid">
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="category1" name="category" value="Accessories" onChange={onCategoryChange} checked={candidato.category === 'Accessories'} />
                            <label htmlFor="category1">Accessories</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="category2" name="category" value="Clothing" onChange={onCategoryChange} checked={candidato.category === 'Clothing'} />
                            <label htmlFor="category2">Clothing</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="category3" name="category" value="Electronics" onChange={onCategoryChange} checked={candidato.category === 'Electronics'} />
                            <label htmlFor="category3">Electronics</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="category4" name="category" value="Fitness" onChange={onCategoryChange} checked={candidato.category === 'Fitness'} />
                            <label htmlFor="category4">Fitness</label>
                        </div>
                    </div>
                </div>

                <div className="formgrid grid">
                    <div className="field col">
                        <label htmlFor="price">Price</label>
                        <InputNumber id="price" value={candidato.price} onValueChange={(e) => onInputNumberChange(e, 'price')} mode="currency" currency="USD" locale="en-US" />
                    </div>
                    <div className="field col">
                        <label htmlFor="quantity">Quantity</label>
                        <InputNumber id="quantity" value={candidato.quantity} onValueChange={(e) => onInputNumberChange(e, 'quantity')} integeronly />
                    </div>
                </div>
            </Dialog>

            <Dialog visible={deleteCandidatoDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteCandidatoDialogFooter} onHide={hideDeleteCandidatoDialog}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem'}} />
                    {candidato && <span>Are you sure you want to delete <b>{candidato.name}</b>?</span>}
                </div>
            </Dialog>

            <Dialog visible={deleteCandidatosDialog} style={{ width: '450px' }} header="Confirm" modal footer={deleteCandidatosDialogFooter} onHide={hideDeleteCandidatosDialog}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem'}} />
                    {candidato && <span>Are you sure you want to delete the selected candidatos?</span>}
                </div>
            </Dialog>
        </div>
    );
}

export default function Cadastro(){
    return <DataTableCrudDemo/>
}
